import React, { Component } from "react";

class About extends Component {
  state = {
    isEmailVis: false,
  };
  render() {
    return (
      <div>
        {this.getEmailText()}
        <button onClick={handleBtnClick} className="btn btn-lg btn-primary">
          E Male
        </button>
      </div>
    );
  }

  handleBtnClick = () => {
    this.setState({ isEmailVis: true });
  };
  getEmailText = () => {
    if (this.state.isEmailVis) {
      return <a href="emailto:">email</a>;
    }
    return <h6>Click on Button</h6>;
  };
}

export default About;
